package Tommy;

public class endswith {

	
	// String starts with and ENDS WITH function and you can see there
	//examples in the below screen.
	
	public endswith() {
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) {

		
		String parrot = "deepa";
		System.out.println(parrot.endsWith("epa"));
		
		String punjab = "delhi";
		System.out.println(punjab.endsWith("lhi"));
		System.out.println(punjab.endsWith("pi"));
		System.out.println(punjab.startsWith("de"));
		System.out.println(punjab.startsWith("del"));
		System.out.println(parrot.startsWith("deep"));
		System.out.println(parrot.startsWith("meena"));

		
		System.out.println(parrot.toUpperCase());
	}

}
